mapper.update:
update members
user_name = #{username}, password = #{password}, email = #{email}, nickname = #{nickname}, phone_number = #{phonenumber},
where u_id = #{uid}

mapper.select:
select token
from members
where u_id = #{uid}

mapper.update:
update members
set token = #{token}
where u_id = #{uid}

update members
set token = #{token, jdbcType=VARCHAR}
where u_id = #{uid}

mapper.select:
select count(u_id)
from members
where u_id = #{uid}
